mapper.select:
SELECT  product_code, product_name, description, price, filename, filesize
FROM product
WHERE product_code = #{product_code}

SELECT filename
FROM product
WHERE product_code=#{product_code}

mapper.update:
	UPDATE product
	SET product_name=#{product_name}
	,price=#{price}
	,description=#{description}
	,filename=#{filename}
	,filesize=#{filesize}
WHERE product_code=#{product_code}
